package javaAPI;

public class Example12 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println("int 范围的最大的值" + Integer.MAX_VALUE);
		System.out.println("int 范围的最小的值" + Integer.MIN_VALUE);
		//把60转变成二进制， 八进制， 十六进制
		System.out.println("二进制" + Integer.toBinaryString(60));
		System.out.println("八进制" + Integer.toOctalString(60));
		System.out.println("十六进制" + Integer.toHexString(60));

	}

}
